import './App.css';
import App from './App';
import Server from './Server';
import Customer from './Customer';
import ReactDOM from 'react-dom/client';
import Manager from "./Manager";
import React, { useState, useEffect } from 'react';

function Add_Seasonal_Menu_Item() {

  var itemArr;
  var newItem;
  var listOfItems;
  var newItemInput;
  var newMenuItemName;
  var newItemIngredientList;

  const [data, setdata] = useState({
    QueryResult: "n/a"
  });

  // Using useEffect for single rendering
  useEffect(() => {
    // Using fetch to fetch the api from 
    // flask server it will be redirected to proxy
    fetch("/data/itemtable").then((res) =>
        res.json().then((data) => {
            // Setting a data from api
            setdata({
                QueryResult: data.QueryResult
            });
        })
    );
}, []);
itemArr = data.QueryResult;
newItem = [];
for (var i = 0; i < itemArr.length; i++){
  newItem.push(itemArr[i].replaceAll("'", "").replaceAll("(", "").replaceAll(")", "").trim().split(','));
}
listOfItems = [];
for (var i = 0; i < newItem.length; i++){
  listOfItems.push(
    {"Name":newItem[i][0],
    "Type":newItem[i][1],
    "Cost":newItem[i][2],
    "Quantity":newItem[i][3],
    "Reorder_Threshold":newItem[i][4]
  }
  )
}

  function handleChange() {

  }



  const [selected, setSelected] = useState('Chicken Sandwich');
  const [price, setPrice] = useState('$4.29');

  function ReturnToManager() {
    const root = ReactDOM.createRoot(document.getElementById('root'));
    root.render(
      <React.StrictMode>
        <Manager />
      </React.StrictMode>
    );
  }


    return (

      <div>
        <header className="SelectRole">
          <div className="flex-container">
        <button type="button" onClick={ReturnToManager}>Return</button>
        </div>
        </header>

        <div>
        <table>
        <tr>
            <td>New Menu Item Name:</td>
            <td><input type="text" value={newMenuItemName}/></td>
          </tr>
          <tr>
            <td>New Menu Item Price:</td>
            <td>$<input type="text" value={newMenuItemName}/></td>
          </tr>
          <tr>
            <td><select value={selected} onChange={handleChange}>
        {listOfItems.map((option) => (
          <option value={option.Name}>{option.Name}</option>
        ))}</select></td>
        <td>       <button type="button" onClick={ReturnToManager}>Add Ingredient</button></td>
          </tr>
          <tr>
            <td><input type="text" value={newItemInput}/></td>
            <td><button type="button" onClick={ReturnToManager}>Add New Ingredient</button></td>
          </tr>
          <tr>
            <td>Menu Item Contents:</td>
            <td>{newItemIngredientList}</td>
          </tr>
        </table>
      </div>



      </div>







    );
  }

export default Add_Seasonal_Menu_Item;
