import './App.css';
import App from './App';
import Server from './Server';
import Customer from './Customer';
import ReactDOM from 'react-dom/client';
import Manager from "./Manager";
import React, { useState, useEffect } from 'react';

function Add_Seasonal_Menu_Item() {

  var itemArr;
  var newItem;
  var listOfItems;
  var selected;

  const [data, setdata] = useState({
    QueryResult: "n/a"
  });

  // Using useEffect for single rendering
  useEffect(() => {
      // Using fetch to fetch the api from 
      // flask server it will be redirected to proxy
      fetch("/data/menutable").then((res) =>
          res.json().then((data) => {
              // Setting a data from api
              setdata({
                  QueryResult: data.QueryResult
              });
          })
      );
  }, []);

  itemArr = data.QueryResult;
  newItem = [];
  for (var i = 0; i < itemArr.length; i++){
    newItem.push(itemArr[i].replaceAll("'", "").replaceAll("(", "").replaceAll(")", "").trim().split(','));
  }
  listOfItems = [];
  for (var i = 0; i < newItem.length; i++){
    listOfItems.push(
      {"Name":newItem[i][0],
      "Composition":newItem[i][1],
      "Cost":newItem[i][2],
    }
    )
  }

  function handleChange() {

  }




  function ReturnToManager() {
    const root = ReactDOM.createRoot(document.getElementById('root'));
    root.render(
      <React.StrictMode>
        <Manager />
      </React.StrictMode>
    );
  }


    return (

      <div>
        <header className="SelectRole">
          <select value={selected} onChange={handleChange}>
        {listOfItems.map((option) => (
          <option value={option.Name}>{option.Name}</option>
        ))}

       </select>
          <div className="flex-container">
        <button type="button" onClick={ReturnToManager}>Return</button>
        </div>
        </header>
      </div>




    );
  }

export default Add_Seasonal_Menu_Item;
